class Solution:
    def maxOperations(self, s: str) -> int:
        s += "1"
        n = len(s)
        ans = 0
        c = 0
        flag = 0
        for i in s:
            if i == '1':
                c += 1
                flag = 0
            else:
                if flag == 0:
                    ans += c
                    flag = 1
        return ans
