const int N = 100010;
class Solution {
public:
    int sum[N], pre[N];
    int maxOperations(string s) {
        int n = s.length();

        int res = 0, cnt = 0;
        for (int i = 0; i < n; i ++ ) {
            if (s[i] == '0') {
                while (i < n && s[i] == '0') i ++ ;
                res += cnt;
                i -- ;
            } else cnt ++ ;
        }


        return res;
    }
};
