class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int last=0;
        int ans=0;
        for(int i=0;i<n;){
            if(s[i]=='1'){
                ++last;
                ++i;
            }
            else {
                int st=i;
                while(i<n && s[st]==s[i]){
                    ++i;
                }
                ans+=last;
            }
        }
        return ans;
    }
};
