class Solution {
public:
    int maxOperations(string s) {
        int cur = 0, ans = 0;
        for (int i = 0 ; i < s.size() ; ++i) {
            if (s[i] == '1') {++cur; continue;}
            if (s[i] == '0' && i > 0 && s[i-1] != '0') {
                ans += cur;
            }
        }
        return ans;
    }
};
