class Solution {
public:
    int maxOperations(string s) {
        int length = s.length();
        int zero_cnt = 0;
        int zero_flg = 1;
        int result = 0;
        for(int i = length - 1 ; i >= 0 ; i --){
            if(s[i] == '1'){
                result += zero_cnt;
                zero_flg = 1;
            }
            else if(s[i] == '0' && zero_flg){
                zero_flg = 0;
                zero_cnt += 1;
            }
        }
        return result;
    }
};
