class Solution {
public:
    int maxOperations(string s) {
        int curr = 0, f = 1;
        long long int ans = 0LL;
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '1') {
                if (!f) ans += curr * 1LL;
                curr++;
                f = 1;
            }
            else f = 0;
        }
        if (!f) ans += (curr * 1LL);
        return ans;
    }
};
