class Solution {
    public int maxOperations(String s) {
        int tot =0;
        int zero = 0;
        int ones=0;

        for (int i=0; i< s.length() ; i++){
            if (s.charAt(i)=='0')
                zero+=1;
            else{
                if (zero!=0)
                    tot = tot + ones;
                ones+=1;
                zero=0;
            }

        }
        int n = s.length();
        if (s.charAt(n - 1)!='0'){
            ones=0;
            zero=0;
        }

        return tot +ones;

    }
}
