class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = 0
        ans = 0
        for i in range(len(s)):
            if s[i] == '1':
                cnt += 1
            if i and s[i] == '0' and s[i - 1] != '0':
                ans += cnt
                # print(i, ans)
        return ans
