class Solution:
    def maxOperations(self, s: str) -> int:
        groups=[]
        i=0
        ans=0
        while i<len(s):
            if s[i]=='1':
                c=0
                while i<len(s) and s[i]=='1':
                    i += 1
                    c += 1
                if i==len(s):
                    break
                groups.append(c)
            else:
                i += 1
        pref=[0]*(len(groups)+1)
        for a in range(len(groups)):
            pref[a+1]=pref[a]+groups[a]
        return sum(pref)
