class Solution {
public:
    int maxOperations(string s) {
        int zeros=0;
        int ones=0;
        int n=s.size();
        int ans=0;
        for(int i=0; i<n; i++){
            if(s[i]=='0'){
                zeros++;
            }
            if(s[i]=='1'){
                if(zeros>0){
                    ans+=ones;
                }
                ones++;
                zeros=0;
            }
        }
        if(zeros>0){
            ans+=ones;
        }
        return ans;
    }
};
