class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0;
        int cnt = 0;
        for(int i=0;i<n;i++){
            if(i && s[i] == '0' && s[i-1] != '0')ans += cnt;
            else if(s[i] == '1')cnt++;
        }
        return ans;
    }
};
