class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, res = 0;
        bool flag = false;
        for (int i = 0; i < s.size(); i++){
            if (s[i] == '1'){
                flag = true;
                cnt++;
            }
            if (s[i] == '0' && flag){
                res += cnt;
                while (s[i + 1] == '0' && i + 1 < s.size()) i++;
            }
        }
        return res;
    }
};
