class Solution {
public:
    int maxOperations(string str) {
        int c = 0, curl = 0 , curr = 0, ans = 0, idx = 0, n = str.length();
        vector< pii > ranges;

        while(idx<n)
        {
            int j = idx;
            while(j<n && str[j]=='1') j++;
            if(str[idx] == '0')
            {

            }else
            {
                ranges.push_back({idx,j-1});
            }
            idx = j+1;
        }

        for(int i=0;i<ranges.size();i++)
        {
            int l = ranges[i].first, r = ranges[i].second;
            int nc = c + (r-l+1);
            l -= c;
            c = nc;

            int len = r - l + 1;
            if(i+1 >= ranges.size())
            {
                if(r != n-1) ans += len;
            }else
            {
                ans += len;
            }
        }

        return ans;

    }
};
