class Solution {
public:
    int maxOperations(string s) {
        int one = 0 ;
        long long ans = 0;
        bool f = false ; 
        for (char c : s) {
            if(c == '1') {
                one++;
                f = true;
            }
            else {
                if(f == true) {
                    ans += one ;
                    f = false ;
                }
            }
        }
        return (int)ans;
    }
};
