class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();

        int ans=0;

        int j=n-1;
        int zcnt=0;

        while (j>=0) {

            if (s[j]=='1') {
                ans+=zcnt;
                j--;
                continue;
            }

            zcnt++;
            while (j>=0 && s[j]=='0') j--;
        }

        return ans;
    }
};
