class Solution {
public:
    int maxOperations(string s) {
        int curr_sm = 0;
        int prev_sm = 0;
        int used_sm = -1;
        int ans = 0;
        for (int i=0; i<s.size(); i++){
            if (s[i] == '1') curr_sm++;
            if (curr_sm == prev_sm && curr_sm == used_sm) continue;
            else{
                if (curr_sm!=prev_sm){
                    prev_sm = curr_sm;
                    continue;
                }
                else if (curr_sm == prev_sm && curr_sm!=used_sm){
                    ans+=curr_sm;
                    used_sm = curr_sm;
                }
            }
        }
        return ans;
    }
};
