class Solution {
public:
    int maxOperations(string s) {
        int i;
        char last;
        long long ans,cnt;
        ans=0;
        cnt=0;
        last='0';
        for (i=0;i<s.length();i++)
        {
            if (s[i]=='1')
            {
                cnt++;
                last='1';
            }
            else
            {
                if (last=='1') ans+=cnt;
                last=s[i];
            }
        }
        return ans;
    }
};
