class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1' && i+1 < s.size() && s[i+1]=='0'){
                cnt ++;
                ans += cnt;
            }else if(s[i]=='1'){
                cnt ++;
            }
        }
        return ans;
    }
};
