class Solution {
    public int maxOperations(String s) {
        int pre = 0;
        int ans = 0;
        boolean has0 = false;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                if (has0) {
                    ans += pre;
                    has0 = false;
                }
                pre++;
            } else {
                has0 = true;
            }
        }
        if (has0) {
            ans += pre;
        }
        return ans;
    }
}
