class Solution {
public:
    int maxOperations(string s) {
        s+="1";
        int n=s.length();
        int c=0, z=0, ans=0;
        for(int i=0; i<n; i++){
            if(s[i] == '0'){
                z++;
            }
            else {
                if(c>0 && z>0) ans += c;
                // cout<<ans<<" "<<z<<"  ";
                c++;
                z=0;
            }
        }
        return ans;
    }
};
