class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.size(), cnt = 0, t = 0;
        bool flag = false;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                flag = true;
                cnt++;
            } else {
                if (flag) {
                    ans += cnt;
                    flag = false;
                }
            }
        }
        return ans;
    }
};
