def split_by_same_elements(lst):
    res = []
    for x in lst:
        if res and x == res[-1][0]:
            res[-1][-1] += 1
            continue
        res.append([x, 1])
    return res


class Solution:
    def maxOperations(self, s: str) -> int:
        s = [0,0] + [int(x) for x in s] + [1,0,0]

        count = s.count(1)

        s = s[::-1]

        res = 0

        for a,b in zip(s, s[1:]):
            if a == 1:
                count -= 1

            if a == b == 1:
                continue
            if a == 1:
                res += count

        return res
