class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, sta = 0;
        for (int i = 0; i + 1 < s.size(); i++){
            if (s[i] == '1'){
                sta++;
                if (s[i + 1] == '0') ans += sta;
            }
        }
        return ans;
    }
};
