class Solution {
public:
    int maxOperations(string s) {
        int perOp = 0;
        int res = 0;

        for (int i = 0; i < s.size() - 1; i++) {
            if (s[i] == '1') {
                perOp++;
                if (s[i+1] == '0') {
                    res += perOp;
                }
            }
        }

        return res;
    }
};
