class Solution {
public:
    using pii=pair<int, int>;

    int maxOperations(string s) {
        s+="1";
        int n=s.size();
        vector<int> b;

        for(int i=0; i<n; i++){
            if(s[i]=='1'){
                int cnt=0;
                int idx=i;
                while(idx<n && s[idx]=='1') ++cnt, ++idx;
                i=idx-1;
                b.push_back(cnt);
            }
        }
        int res=0;
        for(int i=b.size()-1; i>=0; i--){
            res+=b[i]*(b.size()-1-i);
        }
        return res;
    }
};
