class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        vector<int> P;
        for(int i=0;i<n;i++) {
            if (s[i]=='1') P.push_back(i);
        }

        if (P.empty()) return 0;
        P.push_back(n);

        int k=P.size();
        vector<int> num_moves(k,0);
        num_moves[k-1]=0;
        for(int i=k-2;i>=0;i--) {
            num_moves[i]=num_moves[i+1];
            if (P[i+1]-P[i]>=2) {
                num_moves[i]++;
            }
        }
        int ans=0;
        for(int x : num_moves) {
            ans += x;
        }
        return ans;
    }
};
