class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        int n = s.size();
        for(int i = 0; i < s.size(); i ++){
            if(s[i] == '1'){
                cnt ++;
            }else{
                int j = i;
                ans += cnt;
                while(j < n && s[j] == '0'){
                    j ++;
                }
                if(j == n) break;
                i = j - 1;
            }
        }
        return ans;
    }
};
