class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0;

        for (int i = 0; i < s.length(); ++i) {
            if ((s[i] == '0' && i == (s.length() - 1)) || (s[i] == '0' && s[i + 1] == '1')) {
                ans += ones;
                continue;
            } else if (s[i] == '1') ++ones;
        }
        return ans;

    }
};
