class Solution {
public:
    int maxOperations(string s) {
		s.push_back('1');
        vector<int> mv;
        int i=0;
        while(i<s.size() && s[i] == '0'){i++;}
        for (i=0;i<s.size();){
            int j=i;
            while(j<s.size() && s[j] == '1'){j++;}
            if(j > i){mv.push_back(j-i);}
            i=j;
            while(i<s.size() && s[i] == '0'){i++;}
        }
        // for(auto x : mv){printf("%d\n",x);}
        int ans = 0;
		for(i=0;i<mv.size()-1;i++){
			ans += mv[i]*(mv.size()-1-i);
		}
        return ans;
    }
};
