class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        cur = 0
        a = []
        for i in range(n):
            if s[i] == '1':
                cur += 1
            else:
                if cur > 0:
                    a.append(cur)
                    cur = 0
        if cur > 0:
            a.append(cur)
        else:
            a.append(1)
        ans = 0
        s = 0
        for i in range(len(a)):
            ans += s
            s += a[i]
        return ans
