class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(),s.end());
        int res = 0, sum = 0;
        int last = -1;
        for(auto tt : s){
            if(tt == '0'){
                if(last == 0)continue;
                else sum ++;
                last = 0;
            }else{
                res += sum;
                last = 1;
            }
        }
        return res;
    }
};
