class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = cnt = 0

        i = n - 1
        while i >= 0:
            if s[i] == '0':
                cnt += 1
                while i >= 0 and s[i] == '0':
                    i -= 1
            else:
                ans += cnt
                i -= 1
        return ans
