class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int res = 0;
        int cur = 0;
        for (int i = 0; i < n - 1; ++i) {
            if (s[i] == '1') {
                if (s[i + 1] == '0') {
                    cur++;
                    res += cur;
                } else {
                    cur++;
                }
            }
        }
        return res;
    }
};
