class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        string t=s;
        sort(t.begin(),t.end());
        vector<int> pref(n,0);
        if(s[0]=='1') pref[0]=1;

        for(int i=1;i<n;i++){
            pref[i]+=pref[i-1];
            if(s[i]=='1') pref[i]++;
        }

        int cnt=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                if(i-1>=0 && s[i-1]=='1'){
                    cnt+=pref[i];
                }
            }
        }
        return cnt;
    }
};
