class Solution {
public:
    int maxOperations(string s) {


        int n=s.size();
        int lastone=n;
        int count=0;

        int i=n-1;
        int ans=0;

        while(i>=0){

            // cout<<i<<" "<<count<<" "<<ans<<endl;
            if(s[i]=='1'){
                ans+=count;
                i--;
            }
            else if(s[i]=='0'){
                while(i>=0 && s[i]=='0')i--;
                count++;
            }


            // i--;
        }

        return ans;
    }
};
