class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> p;
        for(int i = 0; i < n; ++i)
        {
            if(s[i] == '1')
            {
                int j = i;
                while(j < n && s[j] == '1') j++;
                p.push_back(j - i);
                i = j - 1;
            }
        }
        long long ans = 0;
        if(s[n - 1]!='0')
        p.pop_back();
        reverse(p.begin(), p.end());
        for(int i = 0; i < p.size(); ++i) ans += 1ll * p[i] * (i + 1);
        return ans;
    }
};
