class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();
        bool zero = false;
        int last = 0;
        int total = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (s[i] == '0') {
                zero = true;
                continue;
            }
            if (!zero) {
                continue;
            }
            if (s[i + 1] == '0') {
                ++last;
            }
            total += last;
        }
        return total;
    }
};
