class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int cnt = 0;
        int cur = 0;
        while(cur < n) {
            while(cur < n && s[cur] == '1') {
                cur++;
                cnt++;
            }
            if(cur == n) break;
            while(cur < n && s[cur] == '0') {
                cur++;
            }
            ans += cnt;
        }
        return ans;
    }
};
