class Solution:
    def maxOperations(self, s: str) -> int:

        ones = 0
        res = 0
        s = '0' + s + '1'
        for ii, cc in enumerate(s):
            if ii == 0:
                continue
            prev = s[ii - 1]
            if prev == '0' and cc == '1':
                res += ones
            if cc == '1':
                ones += 1
        return res
