class Solution {
public:
    int maxOperations(string s) {

        int n=s.size();
        int c=s[0]-'0';
        long long ans=0;

        for(int i=1;i<n;i++){
            if(s[i]=='0' && s[i-1]=='1')
                ans+=c;
            if(s[i]=='1')
                c++;
        }
        return ans;
    }
};
