class Solution:
    def maxOperations(self, s: str) -> int:
        A = []
        B = []
        for k,grp in groupby(s):
            if k == '0':
                A.append(0)
            else:
                A.append(len(list(grp)))
                B.append(A[-1])

        ans = s = 0
        for x in B:
            ans += s
            s += x

        if A[-1] == 0:
            ans += s
        return ans
