class Solution {
public:
    int maxOperations(string s) {
        int count=0, ans=0;
        int n=s.size();
        for(int i =0; i < n; i++){
            if(s[i]=='1')count++;
            else if(i>0 and s[i]=='0' and s[i-1]=='1'){
                ans+=count;
            }
        }
        return ans;
    }
};
