class Solution {

    public int maxOperations(String s) {
        int count = 0 , ans = 0;
        boolean hasGap = false;
        for (int i = 0;i < s.length();i ++) {
            if (s.charAt(i) == '1') {
                int current = 0;
                while (i < s.length() && (s.charAt(i) == '1')) {
                    i ++;
                    current ++;
                }
                i --;
                if (hasGap) {
                    ans += count;
                }
                count += current;
                hasGap = false;
            } else {
                hasGap = true;
            }
        }
        if (hasGap) {
            ans += count;
        }
        return ans;
    }

}
