class Solution {
public:
    int maxOperations(string s) {
        int blocks = 0, n = s.length();
        int i = n - 1, ret = 0;
        while(i >= 0) {
            if (s[i] == '0') {
                while(i >= 0 && s[i] == '0') i--;
                blocks++;
            } else {
                ret += blocks;
                i--;
            }
        }
        return ret;
    }
};
