class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        num = []

        j = 0
        for i in range(n):
            if s[i] == '0':
                if i - j:
                    num.append(i - j)
                j = i + 1
        if n - j:
            num.append(n - j)
        else:
            num.append(1)

        s = 0
        ans = 0
        for i in range(len(num)):
            ans += s
            s += num[i]
        return ans
