class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();

        int ans=0;
        int c=0;
        bool flag=true;

        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                c++;
                flag=true;
            }else{
                if(flag)
                ans+=c;

                flag=false;
            }
        }

        return ans;
    }
};
