public class Solution {
    public int MaxOperations(string s) {
        int ops = 0;
        int ones = 0;
        int i = 0;
        while (i < s.Length) {
            if (s[i] == '0') {
                ops += ones;
                while (i < s.Length && s[i] == '0') {
                    ++i;
                }
            } else {
                ++ones;
                ++i;
            }
        }
        return ops;
    }
}
