class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int max_oper = 0;
        int zero_cnt = 0;
        int one_cnt = 0;
        for(int i=0; i<n; i++){
            if(s[i]=='0'){
                zero_cnt++;
            }
            else{
                if(zero_cnt>0){
                    zero_cnt = 0;
                    max_oper += one_cnt;
                }
                one_cnt++;
            }
        }
        if(zero_cnt>0) max_oper += one_cnt;
        return max_oper;
    }
};
