class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int count = 0;
        for(int i = 0; i < s.length() - 1; i++) {
            if(s.charAt(i) == '0') {
                continue;
            }
            count++;
            if(s.charAt(i + 1) == '0') {
                res += count;
            }
        }
        return res;
    }
}
