class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        string str = "";
        int i = 0;
        while(i < n){
            str += s[i];
            if( s[i] == '0'){
                while( i < n and s[i] == '0')   i += 1;
            }
            else    i += 1;
        }
        n = str.size();
        int zero = count( str.begin(), str.end(), '0');
        int ans = 0;
        for( char ch : str){
            if( ch == '1')  ans += zero;
            else    zero -= 1;
        }
        return ans;
    }
};
